  render() {
    return (
      <MainWrapper>
        <SubWrapper>
          <SubLeftWrapper>
            <Logo
              nlogo={nlogo}
            />
          </SubLeftWrapper>
          <SubRightWrapper>
            <MessageIconW>
              <MessageIcon>

                <FontAwesomeIcon icon={faEnvelope} />
                <MessageIconT color={properties.otherBlue}>
                  <TextBox
                    color={properties.white}
                    fontSize={fSize.sSmall}
                  >
                    27
                  </TextBox>
                </MessageIconT>

              </MessageIcon>
            </MessageIconW>
            <NotificationIconW>
              <NotificationIcon>
                <FontAwesomeIcon icon={faBell} />
                <NotificationIconT color={properties.otherBlue}>
                  <TextBox
                    color={properties.white}
                    fontSize={fSize.sSmall}>
                    6</TextBox>
                </NotificationIconT>

              </NotificationIcon>
            </NotificationIconW>
            <ProfileDIv>
              <ProfileDIvWrapper>
                <ProfilePhoto
                  bgimage={bgimage}
                />
                <Profile>
                  <ProfileName>
                    <TextBox
                      color={properties.littleLightGrey}
                      fontSize={fSize.medium}
                    >
                      Jane Pearson
                    </TextBox>
                  </ProfileName>
                  <Designation>
                    <TextBox
                      color={properties.lightGrey}
                      fontSize={fSize.lSmall}
                    >
                      Administrator
                    </TextBox>
                  </Designation>
                </Profile>
              </ProfileDIvWrapper>
            </ProfileDIv>
          </SubRightWrapper>
        </SubWrapper>
      </MainWrapper>
    )
  }
}


export default Header
