import React, { Component } from 'react'
import styled from 'styled-components'
import { properties, fSize, fWeight } from '../../conf/properties'
import TextBox from '../../components/TextBox/TextBox'
import bgimage from '../../assets/images/25.jpg'
import nlogo from '../../assets/images/nlogo.png'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faEnvelope } from "@fortawesome/free-regular-svg-icons"
import { faBell } from "@fortawesome/free-regular-svg-icons"

const MainWrapper = styled.div`
  height : 45px;
  background-color : ${properties.white};
  border-bottom : 1px solid rgba(0, 40, 100, 0.12);
  padding : 16px 8px;
  display: flex;
  flex-direction: row;
  justify-content: center;
`
const SubWrapper = styled.div`
  width: 93%;
  height: inherit;
  background-color : ${properties.white};
  display: flex;
  flex-direction: row;
  align-content: center;
`
const SubLeftWrapper = styled.div`
  width: 50%;
  height: 100%;
  /* background-color: blue; */
`
const SubRightWrapper = styled.div`
  width: 50%;
  height: 100%;
  display: flex;
  flex-direction: row;
  justify-content: flex-end;
  /* background-color: green; */

`

const Logo = styled.div`
background-image: url(${props => props.nlogo});
background-size: cover;
height: 45px;
width: 143px;
line-height: 2rem;
vertical-align: bottom;
margin-right: .5rem;

`
const MessageIconW = styled.div`
  width: 32px;
  height: 45px;
  display: flex;
  justify-content: center;
  align-items: center;
  /* background-color: red; */

`
const MessageIcon = styled.div`
  height: 15px;
  width: 15px;
  color: ${properties.littleLightGrey}

`
const MessageIconT = styled.div`
    float: left;
    /* padding: .2rem .25rem; */
    padding: 3.2px px;
    width: 10px;
    height: 10px;
    background-color: #cd201f !important;
    border-radius: 10rem;
    padding: 0.25em 0.6em;
    font-size: 75%;
    font-weight: 600;
    line-height: 1;
    text-align: center;
    white-space: nowrap;
    vertical-align: baseline;

`
const NotificationIconW = styled.div`
  width: 32px;
  height: 45px;
  display: flex;
  justify-content: center;
  align-items: center;
  /* background-color: blue; */
`
const NotificationIcon = styled.div`
  height: 15px;
  width: 15px;
  color: ${properties.littleLightGrey}

`
const ProfileDIv = styled.div`
/* width: 23.89%; */
width: 143px;
height: 100%;
/* background-color: yellow; */
float: right;
`
const ProfileDIvWrapper = styled.div`
width: -webkit-fill-available;
height: 70%;
/* background-color: red; */
float: right;
padding: 0px 0px 0px 12px;
display: flex;
flex-direction: row;
`
const ProfilePhoto = styled.div`
  width: 2.6rem;
  height: 2rem;
  background-image : url(${props => props.bgimage});
  background-size: cover;
  border-radius : 50%;
  /* background-color: black; */

`
const Profile = styled.div`
/* background-color: rebeccapurple; */
height: 32px;
width: -webkit-fill-available;
margin-left: 0.5rem !important;
`
const ProfileName = styled.div`
/* background-color: yellowgreen; */
height: 19px;
width: -webkit-fill-available;
text-align: left;
font-size: ${fSize.medium};
color: ${properties.littleLightGrey}
`
const Designation = styled.div`
/* background-color: violet; */
height: 13px;
width: -webkit-fill-available;
/* margin: 4px 0px 0px; */
font-size: 13.125px;

`
class Header extends Component {
  render() {
    return (
      <MainWrapper>
        <SubWrapper>
          <SubLeftWrapper>
            <Logo
              nlogo={nlogo}
            />
          </SubLeftWrapper>
          <SubRightWrapper>
            <MessageIconW>
              <MessageIcon>
                <MessageIconT />
                <FontAwesomeIcon icon={faEnvelope} />
              </MessageIcon>
            </MessageIconW>
            <NotificationIconW>
              <NotificationIcon>
                <FontAwesomeIcon icon={faBell} />
              </NotificationIcon>
            </NotificationIconW>
            <ProfileDIv>
              <ProfileDIvWrapper>
                <ProfilePhoto
                  bgimage={bgimage}
                />
                <Profile>
                  <ProfileName>
                    <TextBox
                      color={properties.littleLightGrey}
                      fontSize={fSize.medium}
                    >
                      Jane Pearson
                    </TextBox>
                  </ProfileName>
                  <Designation>
                    <TextBox
                      color={properties.lightGrey}
                      fontSize={fSize.lSmall}
                    >
                      Administrator
                    </TextBox>
                  </Designation>
                </Profile>
              </ProfileDIvWrapper>
            </ProfileDIv>
          </SubRightWrapper>
        </SubWrapper>
      </MainWrapper>
    )
  }
}


export default Header
